package com.hfad.recyclerview;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.util.Log;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {
    private static final String TAG = "MainActivity";
    private ArrayList<String> mNames = new ArrayList<>();
    private ArrayList<String> mImageUrls = new ArrayList<>();

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        Log.d(TAG, "onCreate: started.");
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        initiateImages();

    }

    private void initiateImages(){
        Log.d(TAG, "initiateImages: preparingImages");

        mNames.add("India");
        mImageUrls.add("data:image/png;base64,");

        mNames.add("Russia");
        mImageUrls.add("data:image/png;base64,");

        mNames.add("United States");
        mImageUrls.add("data:image/png;base64,");

        mNames.add("United Kingdom");
        mImageUrls.add("data:image/png;base64,");

        mNames.add("France");
        mImageUrls.add("data:image/png;base64,");

        mNames.add("China");
        mImageUrls.add("data:image/png;base64,");

        Log.d(TAG, "initiateImages: done");
        
        initiateRecyclerView();

    }

    public void initiateRecyclerView()
    {
        RecyclerView recyclerView = findViewById(R.id.recyclerView);
        RecyclerViewAdapter recyclerViewAdapter = new RecyclerViewAdapter(this,mImageUrls,mNames);
        recyclerView.setAdapter(recyclerViewAdapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
    }
}
